input = <<-EOS





EOS

pad = [
  [nil, nil, nil, nil, nil, nil, nil],
  [nil, nil, nil, 1, nil, nil, nil],
  [nil, nil, 2, 3, 4, nil, nil],
  [nil, 5, 6, 7, 8, 9, nil],
  [nil, nil, 'A', 'B', 'C', nil, nil],
  [nil, nil, nil, 'D', nil, nil, nil],
  [nil, nil, nil, nil, nil, nil, nil],
]

coords = [3,1]
Move = {
  'L' => ->{ coords[1] -= 1 if pad[coords[0]][coords[1] - 1] },
  'R' => ->{ coords[1] += 1 if pad[coords[0]][coords[1] + 1] },
  'U' => ->{ coords[0] -= 1 if pad[coords[0] - 1][coords[1]] },
  'D' => ->{ coords[0] += 1 if pad[coords[0] + 1][coords[1]] },
}

code = []

input.each_line do |line|
  coords = [3,1]
  line.each_char do |char|
    next unless Move[char]
    Move[char].call
  end
  p coords
  code << pad[coords[0]][coords[1]]
end

p code
